select Тип_договора,Договор.Номер_договора,Поставщик,[Этап договора].Сумма,Дата_договора,Фактура.Сумма_выполнения from Договор inner join Поставщик
ON Договор.Номер_поставщика=Поставщик.Номер_поставщика join [Этап договора] ON Договор.Номер_договора = [Этап договора].Номер_договора join Фактура on [Этап договора].Номер_этапа=Фактура.Номер_этапа and [Этап договора].Номер_этапа= Фактура.Номер_договора

